<?phpclass Link{	public static function Build($link, $type = 'http')	{		$base = $type . '://' . getenv('SERVER_NAME');				if (defined('HTTP_SERVER_PORT') && HTTP_SERVER_PORT != '80' &&			strpos($base, 'https') === FALSE)		{			$base .= ':' . HTTP_SERVER_PORT;					} 				$link = $base . VIRTUAL_LOCATION . $link;				return htmlspecialchars($link, ENT_QUOTES);	}    public static function ToRegisterCustomer()    {        return self::Build('register-customer/');    }        public static function ToAccount($link = '')    {    	return self::Build('user/' . $link);    }        // Creates a link to the update customer account details page    public static function ToAccountDetails()    {    	return self::Build('account-details/');    }    /*	public static function ToAccountReset()	{		return self::Build('account-reset');	}		public static function ToHomePage()	{		return self::Build('page-homepage');	}		public static function ToManagement($params = '', $step = '0')	{		$retLink = self::Build('page-management');				if ( $params !== '')		{				if ($params == 'new')			{				$retLink .= '/' . 'action-'. $params . '/' . 'step-' . $step;			}			if ($params == 'view')  			{				$retLink .= '/' . 'action-'. $params;			}			if ($params == 'edit')			{				$retLink .= '/' . 'action-'. $params;			}		}							return $retLink;	}		public static function ToSucceedSubmit()	{		$retLink = self::Build('page-management/action-submit/status-succeed');				return $retLink;	}		public static function ToHistory()	{		return self::Build('page-history');	}		public static function ToPasswordReset()	{		return self::Build('page-passwordreset');	}		public static function ToLogout()	{		//return self::Build('account_logged.php?Logout');		return self::Build('logout');	}		public static function ToItemView($recordID)	{		return self::Build('page-management/action-view/item-' . $recordID);	}		public static function ToItemEdit($recor